import React, { useState }  from 'react';
import './App.css';
import CreatureCard from './Components/CreatureCard'
import fish from './CreaturesJSON/FishJSON';
import bugs from './CreaturesJSON/BugsJSON';

const fishList = [...fish];
const bugList = [...bugs];
const filteredFishList = fishList.filter(fish => fish.location = "River");

function App() {
var state = {
  filter: true
}

function FishCards() {
  if (this.state.filter) {
  return 
    ( <div className="fish-container">
    {
      fishList.map(creature => (
        <CreatureCard
          name={creature.name}
          location={creature.location}
          price={creature.price}
        />))}

  </div>)
}}

  return (
    <div className="App">
      <div className="filters">
      <button
        id="riverFishFilter"
        onClick={state.filter=!state.filter}> RIVER FILTER </button>

        <FishCards />
      </div>




      <div className="bugs-container">
        {bugList.map(creature => (
          <CreatureCard
            name={creature.name}
            location={creature.location}
            price={creature.price}
          />
        ))}
      </div>
    </div>
  );
}


export default App;
