import React, { useState }  from 'react';
import './App.css';
import CreatureCard from './Components/CreatureCard'
import fish from './CreaturesJSON/FishJSON';
import bugs from './CreaturesJSON/BugsJSON';

const fishList = [...fish];
const bugList = [...bugs];
const filteredFishList = fishList.filter(fish => fish.location = "River");

function App() {
var state = {
  filter: true
}
var [filtered, setFiltered] = useState(false);

  return (
    <div className="App">
      <div className="filters">
      <input
        id="riverFishFilter"
        type="checkbox"
        onClick={setFiltered(true)}
        ></input>


      </div>

   <div className="fish-container">
        {
          fishList.map(creature => creature.location === "River" && (
            <CreatureCard
              name={creature.name}
              location={creature.location}
              price={creature.price}
            />))}

      </div> 


      <div className="bugs-container">
        {bugList.map(creature => (
          <CreatureCard
            name={creature.name}
            location={creature.location}
            price={creature.price}
          />
        ))}
      </div>
    </div>
  );
}


export default App;
