import React, { useState }  from 'react';
import './App.css';
import CreatureCard from './Components/CreatureCard'
import fish from './CreaturesJSON/FishJSON';
import bugs from './CreaturesJSON/BugsJSON';

const fishList = [...fish];
const bugList = [...bugs];


function App() {
  var filteredFishList = [];
  function filtersChecked() {

    const riverFishFilter = document.getElementById("riverFishFilter");
    if (riverFishFilter.isChecked(true)) {
      filteredFishList.push(fishList.filter(fish => fish.location = "River"));
      return filteredFishList;
    }
  }





  return (
    <div className="App">
      <div className="filters">
      <input 
        type="checkbox"
        id="riverFishFilter"
        onClick={filtersChecked()}></input>
      </div>


      <div className="fish-container">
        {filteredFishList.map(creature => (
          <CreatureCard
            name={creature.name}
            location={creature.location}
            price={creature.price}
          />
        ))}
      </div>

      <div className="bugs-container">
        {bugList.map(creature => (
          <CreatureCard
            name={creature.name}
            location={creature.location}
            price={creature.price}
          />
        ))}
      </div>
    </div>
  );
}


export default App;
