import React  from 'react';
import './App.css';
import CreatureCard from './Components/CreatureCard'
import fishJSON from './CreaturesJSON/FishJSON';


const fishList = [...fishJSON];


class App extends React.Component {
  state = {
      fishToShow: "all"
  }



    render() {
        let fishToShow = [];
        if (this.state.todosToShow === "all") {
            todos = this.state.todos;
        } else if (this.state.todosToShow === "active") {
            todos = this.state.todos.filter(todo => !todo.complete);
        } else if (this.state.todosToShow === "complete") {
            todos = this.state.todos.filter(todo => todo.complete);
        }


        return (
             <div>
                <div
                style={{ 
                    display: "flex",
                    flexWrap: "wrap"

                }}>
                {fishList.map(fishy => 
                    <CreatureCard name={fishy.name} location={fishy.location} price={fishy.price} />)}
                </div>

                <button onClick={this.fishFilter("all")}> all </button>
                <button onClick={this.fishFilter("river")}> River Fish</button>
                <button onClick={this.fishFilter("pond")}> pond Fish</button>

            </div>   
        )

    }
}




export default App
