import React, { useState }  from 'react';
import './App.css';
import CreatureCard from './Components/CreatureCard'
import fish from './CreaturesJSON/FishJSON';
import bugs from './CreaturesJSON/BugsJSON';

const fishList = [...fish];
const bugList = [...bugs];
const searchBar = document.getElementById('searchBar');


searchBar.addEventListener('onKeyUp', (e) => {
  const searchString = e.target.value.toLowerCase();
  const filteredFish = fishList.filter((fish) => {
    return fish.name.toLowerCase().includes(searchString);
  })
})

class App extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    <div className = "App" >
      <div className="search-bar">
        <input
          id="searchBar"
          type="text"
        ></input>


      </div>

      <div className="fish-container">
        {
          this.filteredFish.map(creature => (
            <CreatureCard
              name={creature.name}
              location={creature.location}
              price={creature.price}
            />))}

      </div>


      <div className="bugs-container">
        {bugList.map(creature => (
          <CreatureCard
            name={creature.name}
            location={creature.location}
            price={creature.price}
          />
        ))}
      </div>
    </div>
  ;
}

}




export default App;
