import React, { useState }  from 'react';
import './App.css';
import CreatureCard from './Components/CreatureCard'
import fishJSON from './CreaturesJSON/FishJSON';
import bugsJSON from './CreaturesJSON/BugsJSON';

const fishList = [...fishJSON];
const bugList = [...bugsJSON];

class App extends React.Component {
    state = {
        fishToShow: "all" 
    }
showRiverFish = () => {
    this.setState({
        fishToShow: "river"
    })
}

render() {
    let selectedFishies = [];
    if (state.fishToShow === "river") {
        selectedFishies.push
    }

    return (
        <div> 
            <div> 
                selectedFishies.map(fishy => 
                <CreatureCard name={fishy.name} location={fishy.location} />
                )
            </div>
            <button onClick={this.showRiverFish}>Show River Fish</button>
        </div>

    )

}
}

export default App
